import tinycolor from "tinycolor2";

//#region NoProfileImage
export const noProfilePictureImage = `data:image/png;base64,`;
//#endregion
export function extractInitials(displayName: string | undefined, username: string) {
  return (
    displayName && displayName !== `@${username}`
      ? displayName.split(" ").filter((s, i) => i < 2).map((s) => s[0]).join("")
      : username.substring(0, 2)).toUpperCase();

}

type GenerateAvatarParams = Readonly<{
  initials: string,
  size?: number,
  font?: string,
  baseColor?: tinycolor.Instance,
  theta?: number
}>

export const avatarDefaultParams: Pick<GenerateAvatarParams, "size" | "font"> = {
  size: 200,
  font: `100px "Fira Sans", sans-serif`
}

export function randomAvatarParams(): Pick<GenerateAvatarParams, "baseColor" | "theta"> {
  const h = 360 * Math.random();
  const s = 0.5 + (0.5 * Math.random());
  const l = 0.33 + (0.33 * Math.random());
  return { baseColor: tinycolor({ h, s, l }), theta: 2 * Math.PI * Math.random() };
}

export function generateAvatar(params: GenerateAvatarParams) {
  const { initials, size, font, baseColor, theta } = { ...avatarDefaultParams, ...randomAvatarParams(), ...params };

  const colors = baseColor!.triad().map((t) => t.toHexString());

  const canvas = document.createElement("canvas");
  canvas.height = size!;
  canvas.width = size!;
  const ctx = canvas.getContext("2d");
  const x1 = size! * (0.5 - Math.cos(theta!));
  const x2 = size! * (0.5 + Math.cos(theta!));
  const y1 = size! * (0.5 - Math.sin(theta!));
  const y2 = size! * (0.5 + Math.sin(theta!));
  const gradient = ctx!.createLinearGradient(x1, y1, x2, y2);
  gradient.addColorStop(0, colors[0]);
  gradient.addColorStop(1, colors[2]);
  ctx!.fillStyle = gradient;
  ctx!.fillRect(0, 0, size!, size!);
  ctx!.textAlign = "center";
  ctx!.textBaseline = "middle";
  ctx!.fontKerning = "none";
  ctx!.font = font!;
  ctx!.moveTo(size!, size!);
  ctx!.fillStyle = colors[1];
  ctx!.fillText(initials.toUpperCase(), size!/2, size!/2);
  return canvas.toDataURL();
}
